import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getBoardPost, deleteBoard } from '../api/boards'; // deleteBoard 추가

function BoardPostView() {
  const { boardType, postId } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // 추가 로그: BoardPostView가 마운트될 때 params를 확인
    console.log("BoardPostView mounted with params:", boardType, postId);
    async function fetchPost() {
      try {
        const data = await getBoardPost(boardType, postId);
        console.log("Fetched post:", data); // 기존 로그
        // 추가: 본문 데이터에 이미지 태그가 포함되어 있는지 확인
        console.log("본문 내용:", data.content);
        console.log("본문에 이미지 포함 여부:", data.content.includes("<img"));
        setPost(data);
      } catch (error) {
        console.error("게시글 조회 오류:", error);
      }
    }
    fetchPost();
  }, [boardType, postId]);

  const handleDelete = async () => {
    if (window.confirm("정말 삭제하시겠습니까?")) {
      try {
        await deleteBoard(boardType, postId);
        navigate(`/boards/${boardType}`);
      } catch (error) {
        console.error("게시글 삭제 실패:", error);
      }
    }
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div style={{ margin: "0 auto", maxWidth: "800px" }}>
      {/* 제목 영역: 가운데 정렬 */}
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>{post.title}</h1>

      {/* 새로 추가: 글쓴이가 현재 사용자와 같으면 수정, 삭제 버튼 표시 */}
      {localStorage.getItem('userEmail') === post.author.email && (
        <div style={{ marginBottom: "20px", textAlign: "center" }}>
          <button onClick={() => navigate(`/boards/${boardType}/${postId}/edit`)} style={{ marginRight: "10px" }}>
            수정
          </button>
          <button onClick={handleDelete}>
            삭제
          </button>
        </div>
      )}

      {/* 내용 영역 */}
      <div className="post-content" style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "30px" }}>
        <div dangerouslySetInnerHTML={{ __html: post.content }} />
      </div>
      <style>
        {`
          .post-content img {
            max-width: 100%;
            height: auto;
            display: block;
            margin: 0 auto;
          }
        `}
      </style>

      {/* 댓글 영역: 내용을 밑으로 배치, 동일한 너비 */}
      <div style={{ border: "1px solid #ccc", padding: "10px" }}>
        <h2 style={{ textAlign: "center", marginBottom: "20px" }}>댓글</h2>
        {post.comments && post.comments.length > 0 ? (
          post.comments.map((comment) => (
            <div key={comment._id} style={{ marginBottom: "10px", borderBottom: "1px dashed #ccc", paddingBottom: "5px" }}>
              <p><strong>{comment.author}</strong>: {comment.content}</p>
            </div>
          ))
        ) : (
          <p>댓글이 없습니다.</p>
        )}
        <form style={{ marginTop: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
          <textarea placeholder="댓글 작성..." style={{ width: "100%", height: "80px", marginBottom: "10px" }} />
          <button type="submit">댓글 달기</button>
        </form>
      </div>
    </div>
  );
}

export default BoardPostView;
