import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getBoardPost, deleteBoard } from '../api/boards';
import '../styles/BoardPostView.css'; // Import the CSS file

function BoardPostView() {
  const { boardType, postId } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);

  useEffect(() => {
    console.log("BoardPostView mounted with params:", boardType, postId);
    async function fetchPost() {
      try {
        const data = await getBoardPost(boardType, postId);
        console.log("Fetched post:", data);
        console.log("본문 내용:", data.content);
        console.log("본문에 이미지 포함 여부:", data.content.includes("<img"));
        setPost(data);
      } catch (error) {
        console.error("게시글 조회 오류:", error);
      }
    }
    fetchPost();
  }, [boardType, postId]);

  const handleDelete = async () => {
    if (window.confirm("정말 삭제하시겠습니까?")) {
      try {
        const userEmail = localStorage.getItem('userEmail');
        await deleteBoard(boardType, postId, userEmail);
        navigate(`/boards/${boardType}`);
      } catch (error) {
        console.error("게시글 삭제 실패:", error);
      }
    }
  };

  // 권한 확인 함수 업데이트
  const canEditOrDelete = () => {
    if (!post || !localStorage.getItem('authToken')) return false;

    const currentUserId = localStorage.getItem('userId');
    const currentUserAuthority = parseInt(localStorage.getItem('userAuthority') || '0');

    // 작성자와 현재 로그인한 사용자가 같은 경우
    const isSameUser = post.author && post.author.id === currentUserId;

    // 작성자의 권한 확인 (없으면 0으로 취급)
    const authorAuthority = post.author && post.author.authority ? 
      parseInt(post.author.authority) : 0;

    // 현재 사용자의 권한이 작성자보다 높은 경우
    const hasHigherAuthority = currentUserAuthority > authorAuthority;

    return isSameUser || hasHigherAuthority;
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div className="post-container">
      <div className="post-header">
        <h1 className="post-title">{post.title}</h1>
        {canEditOrDelete() && (
          <div>
            <button 
              onClick={() => navigate(`/boards/${boardType}/${postId}/edit`)} 
              className="action-button edit-button"
            >
              수정
            </button>
            <button 
              onClick={handleDelete}
              className="action-button"
            >
              삭제
            </button>
          </div>
        )}
      </div>

      <div className="post-content">
        <div dangerouslySetInnerHTML={{ __html: post.content }} />
      </div>

      <div className="comment-section">
        <hr className="comment-divider" />

        {post.comments && post.comments.length > 0 ? (
          post.comments.map((comment) => (
            <div key={comment._id} className="comment-item">
              <p><strong>{comment.author}</strong>: {comment.content}</p>
            </div>
          ))
        ) : (
          <p>댓글이 없습니다.</p>
        )}

        {localStorage.getItem('authToken') ? (
          <form className="comment-form">
            <textarea 
              placeholder="댓글 작성..." 
              className="comment-textarea"
            />
            <button type="submit" className="action-button">
              댓글 달기
            </button>
          </form>
        ) : (
          <p className="login-message">댓글을 작성하려면 로그인이 필요합니다.</p>
        )}
      </div>
    </div>
  );
}

export default BoardPostView;
