import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInfo, setUserInfo] = useState({ id: '', authority: 0 });
  const navigate = useNavigate();

  useEffect(() => {
    const checkLoginStatus = () => {
      const token = localStorage.getItem('authToken');
      if (token) {
        setIsLoggedIn(true);
        // 사용자 정보 설정
        setUserInfo({
          id: localStorage.getItem('userId') || '',
          authority: parseInt(localStorage.getItem('userAuthority') || '0')
        });
      } else {
        setIsLoggedIn(false);
        setUserInfo({ id: '', authority: 0 });
      }
    };

    // 초기 로그인 상태 확인
    checkLoginStatus();

    // 로그인 상태 변경 이벤트 리스너
    window.addEventListener('loginStatusChanged', checkLoginStatus);
    return () => {
      window.removeEventListener('loginStatusChanged', checkLoginStatus);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userId');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('userAuthority');

    // 로그인 상태 변경 이벤트 발생
    window.dispatchEvent(new Event('loginStatusChanged'));

    navigate('/');
  };

  return (
    <header className="header">
      <div className="header-container">
        <h1 className="logo">
          <Link to="/">L I K O R E A</Link>
        </h1>
        <nav className="navigation">
          <ul className="nav-items">
            <li><Link to="/">홈</Link></li>
            <li><Link to="/boards/general">일반게시판</Link></li>
            {/* 공지사항 메뉴 제거 */}
            {/* 필요한 다른 게시판 메뉴 추가 */}
            {isLoggedIn && userInfo.authority >= 4 && (
              <li><Link to="/admin">관리자</Link></li>
            )}
          </ul>
        </nav>
        <div className="auth-section">
          {isLoggedIn ? (
            <>
              <span className="user-info">
                {userInfo.id} (Lv.{userInfo.authority})
              </span>
              <button onClick={handleLogout} className="auth-button logout-button">
                로그아웃
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="auth-button login-button">
                로그인
              </Link>
              <Link to="/signup" className="auth-button signup-button">
                회원가입
              </Link>
            </>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
