import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { getBoards } from '../api/boards';

function BoardList() {
  console.log("BoardList 컴포넌트 마운트");
  const { boardType } = useParams();
  console.log("useParams boardType:", boardType);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    console.log("useEffect 실행 - boardType:", boardType);
    const fetchPosts = async () => {
      try {
        const data = await getBoards(boardType);
        console.log("백엔드에서 반환된 데이터:", data);
        // 만약 data가 { posts: [...] } 형태라면 아래와 같이 수정
        setPosts(data.posts || data);
      } catch (error) {
        console.error("게시글 목록 조회 오류:", error);
      }
    };
    fetchPosts();
  }, [boardType]);

  console.log("BoardList 렌더링 - posts 길이:", posts.length);

  return (
    <div>
      <header className="page-header">
        <h1 style={{ textAlign: "center" }}>
          {boardType === "general" ? "일반" : boardType} 게시판
        </h1>
      </header>
      <div>
        <Link to={`/boards/${boardType}/new`} className="write-btn">글쓰기</Link>
      </div>
      {posts.length === 0 ? (
        <p>게시글이 없습니다.</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ borderBottom: "2px solid #000" }}>
              <th style={{ textAlign: "left", padding: "8px" }}>글번호</th>
              <th style={{ textAlign: "left", padding: "8px" }}>제목</th>
              <th style={{ textAlign: "left", padding: "8px" }}>글쓴이</th>
              <th style={{ textAlign: "left", padding: "8px" }}>생성된날짜</th>
              <th style={{ textAlign: "left", padding: "8px" }}>조회수</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post, index) => (
              <tr key={post.id} style={{ borderBottom: "1px solid #ccc" }}>
                <td style={{ padding: "8px" }}>{index + 1}</td>
                <td style={{ padding: "8px" }}>
                  <Link to={`/boards/${boardType}/${post.id}`}>{post.title}</Link>
                </td>
                <td style={{ padding: "8px" }}>{post.author}</td>
                <td style={{ padding: "8px" }}>{post.createdAt}</td>
                <td style={{ padding: "8px" }}>{post.views}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default BoardList;
