import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { BACKEND_URL } from '../config';

// 수정된 함수: 불필요한 줄바꿈과 이미지 단독 블록 태그 제거
function removeUnexpectedBreaks(html) {
  let cleaned = html.replace(/[\r\n]+/g, ' ');
  cleaned = cleaned.replace(/<p>\s*(<img[^>]+>)\s*<\/p>/gi, '$1');
  cleaned = cleaned.replace(/<div>\s*(<img[^>]+>)\s*<\/div>/gi, '$1');
  return cleaned;
}

function BoardDetail() {
  const { boardType, postId } = useParams();
  const [post, setPost] = useState(null);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);
  const [message, setMessage] = useState('');
  // Dummy current user info (in real app, decode token or use context)
  const currentUser = { username: 'currentUser' };

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`${BACKEND_URL}/api/boards/${boardType}/${postId}`);
        if (!response.ok) throw new Error('게시글 조회 실패');
        const data = await response.json();
        setPost(data);
        setComments(data.comments || []);
      } catch (error) {
        console.error(error);
      }
    };
    fetchPost();
  }, [boardType, postId]);

  useEffect(() => {
    if(post) {
      const contentDiv = document.querySelector('.post-content');
      const imgs = contentDiv ? contentDiv.querySelectorAll('img') : [];
      imgs.forEach(img => {
        img.style.display = 'inline'; // 강제로 inline 설정
      });
    }
  }, [post]);

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!localStorage.getItem('authToken')) {
      setMessage('로그인 후 댓글 작성이 가능합니다.');
      return;
    }
    try {
      const response = await fetch(`${BACKEND_URL}/api/boards/${boardType}/${postId}/comments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ comment })
      });
      if (!response.ok) throw new Error('댓글 작성 실패');
      const newComment = await response.json();
      setComments([...comments, newComment]);
      setComment('');
      setMessage('댓글이 작성되었습니다.');
    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div>
      {post ? (
        <>
          <h2>{post.title}</h2>
          {/* 기존 스타일 유지 */}
          <style>{`
            .post-content { white-space: normal; }
            .post-content img { 
              display: inline;
              vertical-align: middle;
              max-width: 100%;
              margin: 0;
            }
            .post-content p:only-child, 
            .post-content div { 
              display: inline;
              margin: 0;
            }
          `}</style>
          <div 
            className="post-content" 
            dangerouslySetInnerHTML={{ __html: removeUnexpectedBreaks(post.content) }} 
          />
          <section>
            <h3>댓글</h3>
            <ul>
              {comments.map(c => (
                <li key={c.id}>
                  <p>{c.text}</p>
                  {/* Allow edit link only if the current user is the author */}
                  {currentUser && c.author === currentUser.username && (
                    <a href="#">[수정]</a>
                  )}
                </li>
              ))}
            </ul>
            <form onSubmit={handleCommentSubmit}>
              <textarea 
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="댓글을 입력하세요"
              />
              <button type="submit">댓글 작성</button>
            </form>
            {message && <p>{message}</p>}
          </section>
        </>
      ) : (
        <p>게시글을 불러오는 중...</p>
      )}
    </div>
  );
}

export default BoardDetail;
