import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getBoardPost, deleteBoard } from '../api/boards'; // deleteBoard 추가

function BoardPostView() {
  const { boardType, postId } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // 추가 로그: BoardPostView가 마운트될 때 params를 확인
    console.log("BoardPostView mounted with params:", boardType, postId);
    async function fetchPost() {
      try {
        const data = await getBoardPost(boardType, postId);
        console.log("Fetched post:", data); // 기존 로그
        // 추가: 본문 데이터에 이미지 태그가 포함되어 있는지 확인
        console.log("본문 내용:", data.content);
        console.log("본문에 이미지 포함 여부:", data.content.includes("<img"));
        setPost(data);
      } catch (error) {
        console.error("게시글 조회 오류:", error);
      }
    }
    fetchPost();
  }, [boardType, postId]);

  const handleDelete = async () => {
    if (window.confirm("정말 삭제하시겠습니까?")) {
      try {
        const userEmail = localStorage.getItem('userEmail');
        await deleteBoard(boardType, postId, userEmail);
        navigate(`/boards/${boardType}`);
      } catch (error) {
        console.error("게시글 삭제 실패:", error);
      }
    }
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div style={{ margin: "0 auto", maxWidth: "800px" }}>
      {/* 제목 영역: 좌측 제목, 우측 수정/삭제 버튼 (플렉스 레이아웃) with extra top margin */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginTop:"20px", marginBottom: "20px" }}>
        <h1 style={{ margin: 0, textAlign: "left", flexGrow: 1 }}>{post.title}</h1>
        {localStorage.getItem('authToken') && localStorage.getItem('userEmail') === post.author.email && (
          <div>
            <button 
              onClick={() => navigate(`/boards/${boardType}/${postId}/edit`)} 
              style={{ 
                marginRight: "10px", 
                padding: "8px 16px",
                fontSize: "1rem"
              }}
            >
              수정
            </button>
            <button 
              onClick={handleDelete}
              style={{ 
                padding: "8px 16px",
                fontSize: "1rem"
              }}
            >
              삭제
            </button>
          </div>
        )}
      </div>

      {/* 내용 영역 - 이미지 인라인 스타일 적용 */}
      <div className="post-content" style={{ marginBottom: "30px" }}>
        <div dangerouslySetInnerHTML={{ __html: post.content }} />
      </div>
      <style>
        {`
          .post-content img {
            max-width: 100%;
            height: auto;
            display: inline;
            vertical-align: middle;
            margin: 0;
          }
        `}
      </style>

      {/* 댓글 영역 - 제목을 구분선으로 변경 */}
      <div style={{ marginBottom: "20px" }}>
        <hr style={{ margin: "20px 0", border: "0", borderTop: "1px solid #ddd" }} />

        {post.comments && post.comments.length > 0 ? (
          post.comments.map((comment) => (
            <div key={comment._id} style={{ marginBottom: "10px" }}>
              <p><strong>{comment.author}</strong>: {comment.content}</p>
            </div>
          ))
        ) : (
          <p>댓글이 없습니다.</p>
        )}

        {localStorage.getItem('authToken') ? (
          <form style={{ marginTop: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
            <textarea 
              placeholder="댓글 작성..." 
              className="comment-textarea"
              style={{ 
                width: "100%", 
                height: "100px", 
                marginBottom: "10px",
                textAlign: "left",
                fontSize: "1rem",
                padding: "0.5rem",
                border: "1px solid #ccc"
              }} 
            />
            <button 
              type="submit" 
              style={{ padding: "8px 16px", fontSize: "1rem" }}
            >
              댓글 달기
            </button>
          </form>
        ) : (
          <p style={{ textAlign: "center", marginTop: "20px" }}>댓글을 작성하려면 로그인이 필요합니다.</p>
        )}
      </div>
    </div>
  );
}

export default BoardPostView;
