import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userAuthority, setUserAuthority] = useState(0);
  const navigate = useNavigate();

  // 컴포넌트 마운트 및 localStorage 변경 시 로그인 상태 확인
  useEffect(() => {
    const checkLoginStatus = () => {
      const token = localStorage.getItem('authToken');
      const authority = localStorage.getItem('userAuthority');
      setIsLoggedIn(!!token);
      setUserAuthority(authority ? parseInt(authority) : 0);
    };

    // 초기 로그인 상태 확인
    checkLoginStatus();

    // localStorage 변경 이벤트 리스너 (다른 탭/창에서 로그아웃 시 동기화)
    const handleStorageChange = () => {
      checkLoginStatus();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    // 로그아웃 처리 - localStorage에서 인증 정보 삭제
    localStorage.removeItem('authToken');
    localStorage.removeItem('userId');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('userAuthority');

    setIsLoggedIn(false);
    setUserAuthority(0);

    // 홈으로 리다이렉트
    navigate('/');
  };

  return (
    <header className="main-header">
      <nav className="main-nav">
        <ul className="nav-list">
          <li className="nav-item">
            <Link to="/" className="nav-link">홈</Link>
          </li>
          <li className="nav-item">
            <Link to="/boards/general" className="nav-link">일반 게시판</Link>
          </li>
          <li className="nav-item">
            <Link to="/boards/notice" className="nav-link">공지사항</Link>
          </li>
          {/* 관리자만 볼 수 있는 메뉴 */}
          {userAuthority >= 5 && (
            <li className="nav-item">
              <Link to="/admin" className="nav-link">관리자</Link>
            </li>
          )}
        </ul>
        <ul className="auth-list">
          {isLoggedIn ? (
            <>
              <li className="auth-item">
                <span className="user-info">
                  {localStorage.getItem('userId')} 
                  ({userAuthority === 5 ? '관리자' : '일반 사용자'})
                </span>
              </li>
              <li className="auth-item">
                <button onClick={handleLogout} className="logout-btn">로그아웃</button>
              </li>
            </>
          ) : (
            <li className="auth-item">
              <Link to="/login" className="auth-link">로그인</Link>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
}

export default Header;
