import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { BACKEND_URL } from '../config';
import { getBoardPost, createBoard, updateBoard } from '../api/boards';

function BoardPostForm() {
  const navigate = useNavigate();
  const { boardType, postId } = useParams(); // postId is optional for editing
  const isEditMode = Boolean(postId);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const contentRef = useRef(null);

  // If editing, fetch post data and update state.
  useEffect(() => {
    if (isEditMode) {
      async function fetchPost() {
        try {
          const data = await getBoardPost(boardType, postId);
          setTitle(data.title);
          setContent(data.content);
          if (contentRef.current) {
            contentRef.current.innerHTML = data.content;
          }
        } catch (error) {
          console.error("게시글 조회 오류:", error);
        }
      }
      fetchPost();
    }
  }, [boardType, postId, isEditMode]);

  // Convert text newlines to <br>
  const convertContentToHtml = (text) => text.replace(/\n/g, '<br>');

  // Handle paste for images.
  const handlePaste = (e) => {
    const items = e.clipboardData.items;
    for (const index in items) {
      const item = items[index];
      if (item.kind === 'file' && item.type.startsWith('image/')) {
        const file = item.getAsFile();
        const reader = new FileReader();
        reader.onload = function(event) {
          console.log("FileReader 결과:", event.target.result);
          const img = document.createElement('img');
          img.src = event.target.result;
          console.log("생성된 img 태그 src:", img.src);
          img.alt = "pasted-image";
          img.style.maxWidth = "100%";
          const selection = window.getSelection();
          if (selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            range.deleteContents();
            range.insertNode(img);
            range.setStartAfter(img);
            selection.removeAllRanges();
            selection.addRange(range);
            const target = e.currentTarget || e.target;
            if (target) {
              target.dispatchEvent(new Event('input', { bubbles: true }));
            }
          } else {
            const target = e.currentTarget || e.target;
            if (target) {
              target.appendChild(img);
              target.dispatchEvent(new Event('input', { bubbles: true }));
            }
          }
        };
        reader.readAsDataURL(file);
        e.preventDefault();
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!localStorage.getItem('authToken')) {
      setMessage('로그인 후 게시글 생성이 가능합니다.');
      return;
    }
    const email = localStorage.getItem('userEmail');
    const currentContent = contentRef.current ? contentRef.current.innerHTML : content;
    console.log("제출 전 content:", currentContent);

    try {
      let response;
      if (isEditMode) {
        // 게시글 수정 API : PUT /api/boards/{boardType}/{postId} 에 title, content, email 전송
        response = await updateBoard(boardType, postId, { title, content: convertContentToHtml(currentContent), email });
      } else {
        response = await createBoard(boardType, { title, content: convertContentToHtml(currentContent), email });
      }
      if (!response) throw new Error(isEditMode ? '게시글 수정 실패' : '게시글 생성 실패');
      setMessage(isEditMode ? '게시글이 수정되었습니다!' : '게시글이 생성되었습니다!');
      console.log(isEditMode ? '게시글 수정 성공:' : '게시글 생성 성공:', response);
      navigate(`/boards/${boardType}`);
    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div style={{ textAlign: "center", fontSize: "1.2rem", margin: "0 auto", maxWidth: "800px", padding: "0 2rem" }}>
      <h2 style={{ textAlign: "center", fontSize: "2rem" }}>
        {isEditMode ? "게시글 수정" : `${boardType === "general" ? "일반" : boardType} 게시판 - 게시글 생성`}
      </h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "1rem" }}>
          <input 
            type="text" 
            placeholder="제목:" 
            value={title} 
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: "100%", fontSize: "1.2rem" }}
          />
        </div>
        <div style={{ marginBottom: "1rem" }}>
          {/* Remove React-managed children; use ref for contentEditable */}
          <div 
            contentEditable
            ref={contentRef}
            suppressContentEditableWarning={true}  // Added to suppress React warning
            onInput={(e) => setContent(e.currentTarget.innerHTML)}
            onPaste={handlePaste}
            style={{ width: "100%", minHeight: "500px", fontSize: "1.2rem", border: "1px solid #ccc", padding: "0.5rem", textAlign: "left" }}
          ></div>
        </div>
        <button type="submit" style={{ fontSize: "1.2rem" }}>
          {isEditMode ? "수정 완료" : "게시글 생성"}
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default BoardPostForm;
